package rosalind.teman;

public class CountingPointMutations {

	public static void main(String[] args) {
		String s = "";
		String t = "";
		int count =0;
		for(int i =0; i< s.length(); i++){
			if(s.charAt(i) != t.charAt(i)){
				count++;
			}
			
		}
		System.out.println(count);
				
	}
	
	
}
